INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (2, 'Карандаш механичкский Skribolino, 1,4мм., Faber-Castell', 17800, 'Специалисты рекомендуют начинать обучение письму карандашом. ﻿Механический карандаш Skribolino от Faber - Castell идеально подходит для этого, так как пишет очень мягко и легко, не царапает бумагу. Таким карандашом можно писать долго без усталости и напряжения, а если сделал ошибку, то ее запросто стереть.', 500, 'Faber-Castell');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (3, 'Стержень для ручки Com4ball Stabilo', 3800, 'Чернила низкой плотности для гладкого и мягкого письма.', 20, 'Com4ball Stabilo');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (4, 'Медиатор для левши, Dunlop', 3500, 'Медиатор для левши пластиковый на большой палец - зеркально перевернутая копия правого. Идеален для игры на гитаре и банджо. Позволит долго играть на инструменте, не думая о натертых пальцах.', 5, 'Dunlop');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (5, 'Клавиатура для левши A4Tech KBS-29 Natural A Left-Handed', 99000, 'Данна клавиатура для левши имеет эргономичную, запатентованную производителем, \"А\"-образную форму расположения клавиш, которая способствует естественному положению кисти рук при работе и снимает лишнюю нагрузку с запястий, мышц рук и шеи.', 83, 'A4Tech');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (6, 'Нож складной для левши Wenger Classic 10', 50000, 'Складной нож Wenger, полностью адаптирован под левую руку. Захваты для открытия лезвий перенесены на противоположную сторону - теперь лезвие можно открыть правой рукой, держа нож в левой.', 71, 'Wenger');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (7, 'Нож складной для левши Wenger Ergo 14', 68000, 'Настоящий швейцарский армейский нож Wenger14, полностью изготовлен для удобства пользования левшей. Захваты для открытия лезвий перенесены на противоположную сторону - теперь лезвие можно открыть правой рукой, держа нож в левой.', 43, 'Wenger');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (8, 'Краски акварельные Faber-Castell Connector, 24 цвета', 43500, 'Сухие акварельные ﻿краски Faber﻿-Castell﻿ Connector.Яркие, насыщенные цвета с высокой степенью непрозрачности.﻿﻿Коробка содержит большие сменные кассеты с красками, которые можно отсоединить и заменить. Дополнительно вложен тюбик с белилами.Внутренняя сторона крышки сделана в виде палитры для смешивания цветов, а на внешней стороне крышки предусмотрено специальное место для написания имени.Коробка закрывается очень плотно, она ни в коем случае не раскроется сама в портфеле или сумке.Идеально подходят для рисования в школе и в детском саду.Легко смываются с одежды.Найдут отличное применение у взрослых, увлекающихся рисованием.Кисточка в комплект не входит.﻿Также есть набор из 12 цветов.Производитель:Faber-Castell, Германия.﻿Вместе с кисточкой Cliсk & Go и стаканчиком для воды от Faber Castell из одного раздела аксессуары образуют замечательный набор для рисования.', 11, 'Faber-Castell');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (9, 'Краски акварельные Faber-Castell Connector, 12 цветов', 27300, 'Сухие акварельные ﻿краски Faber﻿-Castell﻿ Connector.Яркие, насыщенные цвета с высокой степенью непрозрачности.﻿﻿Коробка содержит большие сменные кассеты с красками, которые можно отсоединить и заменить. Дополнительно вложен тюбик с белилами.Внутренняя сторона крышки сделана в виде палитры для смешивания цветов, а на внешней стороне крышки предусмотрено специальное место для написания имени.Коробка закрывается очень плотно, она ни в коем случае не раскроется сама в портфеле или сумке.Идеально подходят для рисования в школе и в детском саду.Легко смываются с одежды.Найдут отличное применение у взрослых, увлекающихся рисованием.Кисточка в комплект не входит.Также бывает набор из 24 цветов.Производитель:Faber-Castell, Германия.﻿Вместе с кисточкой Cliсk & Go и стаканчиком для воды от Faber Castell из одного раздела аксессуары образуют замечательный набор для рисования.﻿', 27, 'Faber-Castell');
INSERT INTO `main`.`product` (`product_id`, `name`, `price`, `description`, `available_number`, `provider_name`) VALUES (10, 'Портняжные ножницы для левши Ringlock 22 см, Premax, Италия', 48900, 'Профессиональные портняжные ножницы для левши Ringlock Professional 220 мм изготовлены с применением передовых технологий:- лезвия соеденины с применением специальных крепежных элементов без использования винта, что отличает их от большинства аналогичных ножниц ещё большей надежностью, прочностью и долговечностью;- лезвия сделаны из облегченных металлов, что позволяет увеличить продуктивность труда, кроме того, данные сплавы обладают отличной прочностью;- лазерная заточка лезвий поможет без лишних усилий делать точные и ровные разрезы;- удобные, эргономичные рукояти облегчат ваш труд - ножницы очень хорошо \"ложатся\" в руку;- красивый дизайн порадует глаз даже самых требовательных покупателей; Технические характеристики:Длина ножниц - 22 см.Отделка рукояти - нейлон.Материал - углеродистая нержавеющая сталь, которая гарантирует высокую стойкость к коррозии и оптимальные режущую способность и выносливость.﻿ Лезвия закалены с  помощью термообработки до твердости 52-26 HRC, чтобы гарантировать хорошее сопротивление резания края.Крепеж лезвий позволяет без проблем осуществлять их последующую заточку.', 106, 'Premax');
